<?php

namespace Aitam\RaccontiBundle\Entity;

use Doctrine\ORM\EntityRepository;

/**
 * RaccontiRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class RaccontiRepository extends EntityRepository {


    public function getphotoracconti($limit = null) {

        $em = $this->getEntityManager();
        $query = $em->createQuery('SELECT r FROM AitamRaccontiBundle:Racconti r WHERE r.isactive = 1 ')
                ->setMaxResults(20);

        try {
            return $query->getResult();
        } catch (\Doctrine\ORM\NoResultException $e) {
            return null;
        }
    }

}
